package com.gear.hotpoom.vo;

public class Address {

	private int no, postNum, mainNum, subNum;
	private String siDo, siGunGu, eupMyeon, roadName;

	public Address() {
		// TODO Auto-generated constructor stub
	}

	public int getNo() {
		return no;
	}

	public void setNo(int no) {
		this.no = no;
	}

	public int getPostNum() {
		return postNum;
	}

	public void setPostNum(int postNum) {
		this.postNum = postNum;
	}

	public int getMainNum() {
		return mainNum;
	}

	public void setMainNum(int mainNum) {
		this.mainNum = mainNum;
	}

	public int getSubNum() {
		return subNum;
	}

	public void setSubNum(int subNum) {
		this.subNum = subNum;
	}

	public String getSiDo() {
		return siDo;
	}

	public void setSiDo(String siDo) {
		this.siDo = siDo;
	}

	public String getSiGunGu() {
		return siGunGu;
	}

	public void setSiGunGu(String siGunGu) {
		this.siGunGu = siGunGu;
	}

	public String getEupMyeon() {
		return eupMyeon;
	}

	public void setEupMyeon(String eupMyeon) {
		this.eupMyeon = eupMyeon;
	}

	public String getRoadName() {
		return roadName;
	}

	public void setRoadName(String roadName) {
		this.roadName = roadName;
	}
	
}
